import FlexWorker from 'sd/FlexWorker';

export default function frisk(config, callback){

    var paths = [];

    for (var i in config.bundles){
        for (var j in config.bundles[i].resources){
            paths.push(config.bundles[i].path + '/' + config.bundles[i].resources[j]);
        }
    }

    var frisker = new FlexWorker('sd/frisk/frisker');

    frisker.postMessage({cmd: 'watch', paths: paths});

    callback(null, frisker);
}
