import log from 'sd/log';

export default function text(mid, callback){

    var isBrowser = typeof window != 'undefined';

    if (isBrowser) {
        //TODO: load text via websockets
    }

    if (!isBrowser) {

        //load text using node fs
        System.node.fs.readFile(System.locate({name: mid}).slice(0, -3), function(err, data){
            if (err) {log(err); callback(err); return;}
            callback(null, data.toString());
        })
    }
}
