export default (pathname) => {
    switch (pathname.split('.').pop()){
        case 'html':
            return 'text/html';
            break;
        case 'png':
            return 'image/png';
            break;
        case 'js':
            return 'text/javascript';
            break;
        case 'css':
            return 'text/css';
            break;
        default:
            return 'text/plain';
    }
}
