import {isBrowser} from 'up/env';

var browser = (mid) => {
    //TODO: check a mid exists
};

var node = (mid) => {
    //check a file exists using node fs
    //returns a thunk
    return (callback) => {

        System.require('fs').stat(
            System.locate({name: mid}).slice(0, -3),
            (err, stat) => {
                callback(!!!err);
            }
        )
    }
};

export default isBrowser ? browser : node;
