import {decode} from 'up/yamlish';
import text from 'up/text';

var load = (mid) => {
    //load yaml file and decode to an plain object
    //returns a thunk
    return (callback) => {
        text(mid)((str) => {
            callback(decode(str))
        })
    }
};

